const { createGlobalStyle } = require('styled-components');

export const GlobalStyle = createGlobalStyle`

${''}

:root{
     /* Colors */
  --orange:#FE8800;
  --deep-blue:#000432;
  --peach:#FFF9F6;
  --light-grey:#DADADA;
}

${''}

*,*::before,*::after{
    margin:0;
    padding:0;
    box-sizing:border-box;
    font-family: 'Poppins', sans-serif;
}

html{
  scroll-behavior:smooth;
}
    body {
        overflow-x: hidden;
    }
    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        margin:0;
        padding:0;
    }
    a {
        text-decoration: none;
    }
    button{
        border:none;
        outline:none;
        &:focus{
            outline:none;
        }
    }

    *:focus {
        outline: none;
    }

    img{
        width:80%;
        hight:auto;
    }
`
