// This is Contact component, it will contain contact form.
import fridge from "../Images/appliances/lg-refrigerator.jpg"
import washer from "../Images/appliances/washer.jpg"
import dryer from "../Images/appliances/samsung-washer-and-dryer.jpg"
import range from "../Images/appliances/bertazzoni-range.jpg"
import cooktop from "../Images/appliances/gas-cooktop.jpg"
import oven from "../Images/appliances/bosch-oven.jpg"
import disposal from "../Images/appliances/garbage-disposal.jpg"
import center from "../Images/appliances/laundry-center.jpg"
import dishwasher from "../Images/appliances/bosch-dishwasher.jpg"
import {imageOptions} from "./GalleryData"
import {textOptions} from "./GalleryData"

import React from "react";
import "./Gallery.css";

const Gallery = () => {
    function runGallery() {
      const container = document.getElementById("gallery-container");

      const displayDescription = () => {
        const description = document.getElementById('gallery-description')
        description.style.display = "flex";
        container.removeEventListener("click", displayDescription);
      }

      container.addEventListener("click", displayDescription);

      container.addEventListener("click", (e) => {
        if (e.target.nodeName == "IMG" || e.target.nodeName == "P") {
          changeImage(Number(e.target.parentElement.getAttribute('value')));
          ScrollDown()
        }
      });

      container.addEventListener("mouseover", (e) => {
        if (e.target.nodeName == "IMG" || e.target.nodeName == "P") {
          const text = e.target.parentElement.querySelector('.card-text');
          text.style.transform = "translate3d(0, -100%, 0)";
        }
      });

      container.addEventListener("mouseout", (e) => {
        if (e.target.nodeName == "IMG" || e.target.nodeName == "P") {
          const text = e.target.parentElement.querySelector('.card-text');
          text.style.transform = "translate3d(0, 0, 0)";
        }
      });

      const images = document.getElementsByClassName("gallery-image");
      for (let image of images) {
        image.addEventListener("mouseover", (e) => {
          for (let image of images) {
            if (image !== e.target) {
              image.style.opacity = 0.6
              image.style.filter = "grayscale(.8)"
            }
          }
        })
        image.addEventListener("mouseout", (e) => {
          for (let image of images) {
            if (image !== e.target) {
              image.style.opacity = 1;
              image.style.filter = "grayscale(0)"
            }
          }
        })
      }
    };

    function ScrollDown() {
      const element = document.getElementById("gallery-description");
      element.scrollIntoView({
        behavior:"smooth",
        block:"start",
        inline:"nearest"
      })
    }

    function changeImage(pos) {
      const descriptionImageOne = document.getElementById("image-one");
      const descriptionImageTwo = document.getElementById("image-two");
      const descriptionImageThree = document.getElementById("image-three");
      const descriptionText = document.getElementById("description-text");
      console.log(imageOptions[pos][0])
      descriptionImageOne.src = imageOptions[pos][0];
      descriptionImageTwo.src = imageOptions[pos][1];
      descriptionImageThree.src = imageOptions[pos][2];
      descriptionText.textContent = textOptions[pos];
    };

  return <div id="gallery" >
    <div id="gallery-container">
      <h1 id="gallery-title">Our services</h1>
      <div class="gallery-row">
        <span class="filter" value="0"><img class="gallery-image" src={fridge}/><p class="card-text" >Fridge repair</p></span>
        <span class="filter" value="1"><img class="gallery-image" src={washer}/><p class="card-text">Washer repair</p></span>
        <span class="filter" value="2"><img class="gallery-image" src={dryer}/><p class="card-text">Dryer repair</p></span>
      </div>
      <div class="gallery-row">
        <span class="filter" value="3"><img class="gallery-image" src={range}/><p class="card-text">Range repair</p></span>
        <span class="filter" value="4"><img class="gallery-image" src={cooktop}/><p class="card-text">Cooktop repair</p></span>
        <span class="filter" value="5"><img class="gallery-image" src={oven}/><p class="card-text">Built-in oven repair</p></span>
      </div>
      <div class="gallery-row">
        <span class="filter" value="6"><img class="gallery-image" src={dishwasher}/><p class="card-text">Dishwasher repair</p></span>
        <span class="filter" value="7"><img class="gallery-image" src={center}/><p class="card-text">Laundry center repair</p></span>
        <span class="filter" value="8" onLoad={runGallery}><img class="gallery-image" src={disposal}/><p class="card-text">Garbage disposal replacement</p></span>
      </div>
    </div>
    <div id="gallery-description" >
      <div id="pop-up">
        <div id="image-section">
          <img class="description-image" id="image-one" src=""/>
          <img class="description-image" id="image-two" src=""/>
          <img class="description-image" id="image-three" src=""/>
        </div>
        <div id="description-text">
          <h3></h3>
          <p></p>
        </div>
      </div>
    </div>
  </div>;
};

export default Gallery;
