import React, { useEffect, useState, useRef } from "react";
import range from "../Images/appliances/bertazzoni-range.jpg"
import cooktop from "../Images/appliances/gas-cooktop.jpg"
import oven from "../Images/appliances/bosch-oven.jpg"
import disposal from "../Images/appliances/garbage-disposal.jpg"
import center from "../Images/appliances/laundry-center.jpg"

import "./Reviews.css"

const Reviews = () => {const sliderRef = useRef(null);
  const sliderItemsRef = useRef(null);
  const prevRef = useRef(null);
  const nextRef = useRef(null);

  const [posX1, setPosX1] = useState(0);
  const [posX2, setPosX2] = useState(0);
  const [posInitial, setPosInitial] = useState(0);
  const [posFinal, setPosFinal] = useState(0);
  const [threshold] = useState(100);
  const [index, setIndex] = useState(0);
  const [allowShift, setAllowShift] = useState(true);

  const slide = (wrapper, items, prev, next) => {
    items.onmousedown = dragStart;

    // Touch events
    items.addEventListener('touchstart', dragStart);
    items.addEventListener('touchend', dragEnd);
    items.addEventListener('touchmove', dragAction);

    // Click events
    prev.addEventListener('click', () => shiftSlide(-1));
    next.addEventListener('click', () => shiftSlide(1));

    // Transition events
    items.addEventListener('transitionend', checkIndex);

    function dragStart(e) {
      e = e || window.event;
      e.preventDefault();
      setPosInitial(items.offsetLeft);

      if (e.type === 'touchstart') {
        setPosX1(e.touches[0].clientX);
      } else {
        setPosX1(e.clientX);
        document.onmouseup = dragEnd;
        document.onmousemove = dragAction;
      }
    }

    function dragAction(e) {
      e = e || window.event;

      if (e.type === 'touchmove') {
        setPosX2(posX1 - e.touches[0].clientX);
        setPosX1(e.touches[0].clientX);
      } else {
        setPosX2(posX1 - e.clientX);
        setPosX1(e.clientX);
      }
      items.style.left = items.offsetLeft - posX2 + 'px';
    }

    function dragEnd(e) {
      setPosFinal(items.offsetLeft);
      if (posFinal - posInitial < -threshold) {
        shiftSlide(1, 'drag');
      } else if (posFinal - posInitial > threshold) {
        shiftSlide(-1, 'drag');
      } else {
        items.style.left = posInitial + 'px';
      }

      document.onmouseup = null;
      document.onmousemove = null;
    }

    function shiftSlide(dir, action) {
      items.classList.add('shifting');

      if (allowShift) {
        if (!action) setPosInitial(items.offsetLeft);

        if (dir === 1) {
          items.style.left = posInitial - items.offsetWidth + 'px';
          setIndex(index + 1);
        } else if (dir === -1) {
          items.style.left = posInitial + items.offsetWidth + 'px';
          setIndex(index - 1);
        }
      }

      setAllowShift(false);
    }

    function checkIndex() {
      items.classList.remove('shifting');

      if (index === -1) {
        items.style.left = -(slidesLength * items.offsetWidth) + 'px';
        setIndex(slidesLength - 1);
      }

      if (index === slidesLength) {
        items.style.left = -items.offsetWidth + 'px';
        setIndex(0);
      }

      setAllowShift(true);
    }
  };

  slide(sliderRef.current, sliderItemsRef.current, prevRef.current, nextRef.current);

  return (
    <div id="slider" className="slider">
      <div className="wrapper">
        <div id="slides" className="slides">
          <span className="slide">Slide 1</span>
          <span className="slide">Slide 2</span>
          <span className="slide">Slide 3</span>
          <span className="slide">Slide 4</span>
          <span className="slide">Slide 5</span>
        </div>
      </div>
      <a id="prev" className="control prev"></a>
      <a id="next" className="control next"></a>
    </div>
  );
};

export default Reviews;
