import React, { useEffect } from "react";
import range from "../Images/appliances/bertazzoni-range.jpg"
import cooktop from "../Images/appliances/gas-cooktop.jpg"
import oven from "../Images/appliances/bosch-oven.jpg"
import disposal from "../Images/appliances/garbage-disposal.jpg"
import center from "../Images/appliances/laundry-center.jpg"

import "./Reviews.css"

const Reviews = () => {
  var slider = document.getElementById('slider'),
    sliderItems = document.getElementById('slides'),
    prev = document.getElementById('prev'),
    next = document.getElementById('next');

function slide(wrapper, items, prev, next) {
  const sliderRef = useRef(null);
  const sliderItemsRef = useRef(null);
  const prevRef = useRef(null);
  const nextRef = useRef(null);

  const [posX1, setPosX1] = useState(0);
  const [posX2, setPosX2] = useState(0);
  const [posInitial, setPosInitial] = useState(0);
  const [posFinal, setPosFinal] = useState(0);
  const [threshold] = useState(100);
  const [slideSize, setSlideSize] = useState(0);
  const [index, setIndex] = useState(0);
  const [allowShift, setAllowShift] = useState(true);

  useEffect(() => {
    const items = sliderItemsRef.current;

    const dragStart = (e) => {
      e.preventDefault();
      setPosInitial(items.offsetLeft);

      if (e.type === 'touchstart') {
        setPosX1(e.touches[0].clientX);
      } else {
        setPosX1(e.clientX);
        document.onmouseup = dragEnd;
        document.onmousemove = dragAction;
      }
    };

    const dragAction = (e) => {
      if (e.type === 'touchmove') {
        setPosX2(posX1 - e.touches[0].clientX);
        setPosX1(e.touches[0].clientX);
      } else {
        setPosX2(posX1 - e.clientX);
        setPosX1(e.clientX);
      }
      items.style.left = `${items.offsetLeft - posX2}px`;
    };

    const dragEnd = (e) => {
      setPosFinal(items.offsetLeft);
      if (posFinal - posInitial < -threshold) {
        shiftSlide(1, 'drag');
      } else if (posFinal - posInitial > threshold) {
        shiftSlide(-1, 'drag');
      } else {
        items.style.left = `${posInitial}px`;
      }

      document.onmouseup = null;
      document.onmousemove = null;
    };

    const shiftSlide = (dir, action) => {
      items.classList.add('shifting');

      if (allowShift) {
        if (!action) setPosInitial(items.offsetLeft);

        if (dir === 1) {
          items.style.left = `${posInitial - slideSize}px`;
          setIndex(index + 1);
        } else if (dir === -1) {
          items.style.left = `${posInitial + slideSize}px`;
          setIndex(index - 1);
        }
      }

      setAllowShift(false);
    };

    const checkIndex = () => {
      items.classList.remove('shifting');

      if (index === -1) {
        items.style.left = `-${sliderItemsRef.current.offsetWidth}px`;
        setIndex(sliderItemsRef.current.children.length - 1);
      }

      if (index === sliderItemsRef.current.children.length) {
        items.style.left = `-${slideSize}px`;
        setIndex(0);
      }

      setAllowShift(true);
    };

    const itemsEl = sliderItemsRef.current.getElementsByClassName('slide');
    const firstSlide = itemsEl[0];
    const lastSlide = itemsEl[itemsEl.length - 1];

    const cloneFirst = firstSlide.cloneNode(true);
    const cloneLast = lastSlide.cloneNode(true);

    items.appendChild(cloneFirst);
    items.insertBefore(cloneLast, firstSlide);
    setSlideSize(itemsEl[0].offsetWidth);

    items.addEventListener('mousedown', dragStart);
    items.addEventListener('touchstart', dragStart);
    items.addEventListener('touchend', dragEnd);
    items.addEventListener('touchmove', dragAction);

    prevRef.current.addEventListener('click', () => shiftSlide(-1));
    nextRef.current.addEventListener('click', () => shiftSlide(1));

    items.addEventListener('transitionend', checkIndex);

    return () => {
      items.removeEventListener('mousedown', dragStart);
      items.removeEventListener('touchstart', dragStart);
      items.removeEventListener('touchend', dragEnd);
      items.removeEventListener('touchmove', dragAction);
    };
  }, [allowShift, index, posFinal, posInitial, posX1, posX2, slideSize]);
}

slide(slider, sliderItems, prev, next);
  return <div id="slider" class="slider">
  <div class="wrapper">
    <div id="slides" class="slides">
      <span class="slide">Slide 1</span>
      <span class="slide">Slide 2</span>
      <span class="slide">Slide 3</span>
      <span class="slide">Slide 4</span>
      <span class="slide">Slide 5</span>
    </div>
  </div>
  <a id="prev" class="control prev"></a>
  <a id="next" class="control next"></a>
</div>
}


export default Reviews;
