// This is About component, It will contain about us info.
import React from "react";
import styled from "styled-components";

const Steps = styled.div`
display: flex;
justify-conten: center;
align-items: center;
height: 40vh;
width: 100vw;

p{
  size: 6px;
}
`

const Step = styled.div`
margin: 10%;
`

const About = () => {
  return <Steps>
    <Step>Breakdown analysis
STEP 1
<p>We will contact you within 30 minutes and listen to your problem.</p></Step>
    <Step>Departure of master
STEP 2
Our specialist will come to you and make a detailed diagnosis.</Step>
    <Step>Repair of appliance
STEP 3
The master will perform the necessary repairs right in your home.</Step>
    <Step>Issuing a guarantee
STEP 4
After the repair is completed, you will receive a warranty.</Step>
    <div>About Component</div>
  </Steps>
};

export default About;
