import React, { useEffect, useRef, useState } from 'react';
import './HighendAddon.css'
import range1 from '../Images/appliances/thermador-range.webp'
import cooktop from '../Images/appliances/jennair-cooktop.png'
import range2 from '../Images/appliances/viking-range.jpg'
import refrigerator from '../Images/appliances/true-refrigerator.jpg'

const HighendAddon = () => {
  const [isVisible1, setIsVisible1] = useState(false);
  const [isVisible2, setIsVisible2] = useState(false);
  const [isVisible3, setIsVisible3] = useState(false);
  const [isVisible4, setIsVisible4] = useState(false);
  const elementRef1 = useRef(null);
  const elementRef2 = useRef(null);
  const elementRef3 = useRef(null);
  const elementRef4 = useRef(null);

  const onIntersection = (entries) => {
    const [entry] = entries;
    setIsVisible1(entry.isIntersecting);
  };
  useEffect(() => {
    const observer = new IntersectionObserver(onIntersection);
    if (elementRef1.current) observer.observe(elementRef1.current);

    // return () => { if (elementRef.current) observer.unobserve(elementRef.current); };

  }, [elementRef1, elementRef2, elementRef3, elementRef4]);


  return <div id="addons">
    <div id="addon-one">
      <div class="first-half">
        <div ref={elementRef1} className={isVisible1 ? 'highend-addon-info animate-left' : 'highend-addon-info'}>
          <h1>We repair all brands and types of large gas and electric cooking appliances</h1>
        </div>
        <div className={isVisible1 ? 'animate-right' : ''} id="addon-image-one">
          <div class="image-frame"><img class="highend-addon-image" src={range1} alt='Stainless steel Thermador range'/></div>
        </div>
      </div>
      <div class="second-half">
        <div ref={elementRef2} className={isVisible2 ? 'animate-left' : ''} id="addon-image-two">
        <div class="image-frame"><img class="highend-addon-image" src={cooktop} alt='Stainless steel Jenn-Air cooktop'/></div>
        </div>
        <div className={isVisible2 ? 'highend-addon-info animate-right' : 'highend-addon-info'}> 
          <h1>Most repairs come with a one-year warranty, satisfaction guearanteed</h1>
        </div>
      </div>
    </div>
    <div id="addon-two">
      <div class="first-half">
        <div ref={elementRef3} className={isVisible3 ? 'highend-addon-info animate-left' : 'highend-addon-info'}>
          <h1>We will take care of your stove, cooktop, and oven</h1>
        </div>
        <div className={isVisible3 ? 'animate-right' : ''} id="addon-image-one">
        <div class="image-frame"><img class="highend-addon-image" src={range2} alt='Stainless steel Viking range'/></div>
        </div>
      </div>
      <div class="second-half">
        <div ref={elementRef4} className={isVisible4 ? 'animate-left' : ''} id="addon-image-two">
        <div class="image-frame"><img class="highend-addon-image" src={refrigerator} alt='Fridge, freezer, wine cooler set up from True residential'/></div>
        </div>
        <div className={isVisible4 ? 'highend-addon-info animate-right' : 'highend-addon-info'}>  
          <h1>As well as of the refrigerator, wine cooler, or freezer</h1>
        </div>
      </div>
    </div>
  </div>
};

export default HighendAddon;
