import { useRef, useEffect } from 'react';

const useLockScrollAtBounds = () => {
    const scrollRef = useRef(null);

    const onScroll = (e) => {
        const element = scrollRef.current;
        if (!element) return;

        const atTop = element.scrollTop === 0;
        const atBottom = element.scrollHeight - element.scrollTop === element.clientHeight;

        if (atTop || atBottom) {
            e.preventDefault();
        }
    };

    useEffect(() => {
        const currentElement = scrollRef.current;
        currentElement?.addEventListener('touchmove', onScroll, { passive: false });

        return () => {
            currentElement?.removeEventListener('touchmove', onScroll);
        };
    }, []);

    return scrollRef;
};

export default useLockScrollAtBounds;
