// This is Contact component, it will contain contact form.
import React from "react";
import styled from "styled-components";
import Google from '../../MySite/googlemaps.png'
import Yelp from '../../MySite/yelp.png'

const ContactSection = styled.div`
width: 100vw;
padding: calc(2.5rem + 2.5vw) 0;
background-color: grey;
display:flex;
flex-direction: column;
justify-content: center;
align-items: center;
`
const Frame = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 60vw;
  padding: 4rem;
  background-color: #003889;
  border: 1px solid black;
  border-radius: 16px;
  box-shadow: 15px 15px 15px black;
`
const Title = styled.h1`
display: inline-block;
font-size: calc(1rem + 1.5vw);
margin-top: 1rem;
margin-bottom: 3rem;
position: relative;
color: white;
text-shadow: 0 0 5px black, 0 0 5px black;


&::before {
  content: '';
  height: 1px;
  width: 50%;
  position: absolute;
  left: 50%;
  bottom: 0;
  border-bottom: 2px solid var(--pink)
  transform: translate(-50%);
}
`
const Icons = styled.div`
  display: inline;
  margin-top: 2rem;
  a {
    &:not(:last-child) {
      margin-right: 2rem;
    }
  &:hover{
    img{
      filter: sepia(30%) saturate(500%) 
    }
  }
  }
  img {
    width: 3rem;
    height: auto;
  }
`
const Form = styled.form`
display:flex;
flex-direction: column;
padding:2rem
margin-bottom:1rem
background-color:blue;

input{
  padding: 1 rem calc(0.5rem + 1vw)
  background-color: white;
  border:none;
  border-radius: 3px;

  &:active,
  &:focus {
    border:none;
    background-color: #CACACA
  }
}
  textarea{
  padding: 1 rem calc(0.5rem + 1vw)
  background-color: white;
  border-radius: 3px;
  border:none;

  &:active,
  &:focus {
    border:none;
    background-color: #CACACA
}
}
label{
  color: white;
  margin-left: 1rem;
  margin-bottom: -1.5rem 
}
button{
  padding: 1rem 2 rem;
  background-color: var(--orange);
  color: black;
  border-radius: 3px;
  font-size: 1.5rem.
  cursor:pointer

}
`
const Contact = () => {
  return <ContactSection>
    <Frame>
    <Title>Request Service</Title>
    <Form>
      <label for="name">Name</label><br></br>
      <input type="text" name="name" id="name"/><br></br>    
      <label for="phone">Phone number</label><br></br>
      <input type="tel" name="phone" id="phone"/><br></br>
      <label for="email">Email</label><br></br>
      <input type="email" name="email" id="email"/><br></br>
      <label for="address">Address</label><br></br>
      <input type="text" name="address" id="address"/><br></br>
      <label for="zip">Zip code</label><br></br>
      <input type="text" name="zip" id="zip"/><br></br>
      <label for="brand">Brand and type of appliance</label><br></br>
      <input type="text" name="brand" id="brand" placeholder="LG refrigerator"/><br></br>
      <label for="problem">Problem and description</label><br></br>
      <textarea name="problem" id="problem" placeholder="Not cooling" rows="4"/><br></br>
      <button>Send the request</button>
    </Form>
    <Icons>
      <a href="https://www.yelp.com/biz/major-appliance-repair-everett-2">
        <img src={Yelp} alt="Yelp" />
      </a>
      <a href="https://goo.gl/maps/ZLRVKagXNCkL9GPL9">
        <img src={Google} alt="Google" />
      </a>

    </Icons>
    </Frame>
  </ContactSection>;
};

export default Contact;
