import React, { useEffect, useRef } from "react";
import range from "../Images/appliances/bertazzoni-range.jpg"
import cooktop from "../Images/appliances/gas-cooktop.jpg"
import oven from "../Images/appliances/bosch-oven.jpg"
import disposal from "../Images/appliances/garbage-disposal.jpg"
import center from "../Images/appliances/laundry-center.jpg"

import "./Reviews.css"

const Reviews = () => {
  var slider = document.getElementById('slider'),
    sliderItems = document.getElementById('slides'),
    prev = document.getElementById('prev'),
    next = document.getElementById('next');

    const Slider = () => {
      const [posX1, setPosX1] = useState(0);
      const [posX2, setPosX2] = useState(0);
      const [posInitial, setPosInitial] = useState(0);
      const [posFinal, setPosFinal] = useState(0);
      const [threshold] = useState(100);
      const [index, setIndex] = useState(0);
      const [allowShift, setAllowShift] = useState(true);

      const wrapperRef = useRef(null);
      const itemsRef = useRef(null);

      const dragStart = (e) => {
        e.preventDefault();
        setPosInitial(itemsRef.current.offsetLeft);

        if (e.type === 'touchstart') {
          setPosX1(e.touches[0].clientX);
        } else {
          setPosX1(e.clientX);
          document.onmouseup = dragEnd;
          document.onmousemove = dragAction;
        }
      };

      const dragAction = (e) => {
        e = e || window.event;

        if (e.type === 'touchmove') {
          setPosX2(posX1 - e.touches[0].clientX);
          setPosX1(e.touches[0].clientX);
        } else {
          setPosX2(posX1 - e.clientX);
          setPosX1(e.clientX);
        }
        itemsRef.current.style.left = `${itemsRef.current.offsetLeft - posX2}px`;
      };

      const dragEnd = () => {
        setPosFinal(itemsRef.current.offsetLeft);

        if (posFinal - posInitial < -threshold) {
          shiftSlide(1, 'drag');
        } else if (posFinal - posInitial > threshold) {
          shiftSlide(-1, 'drag');
        } else {
          itemsRef.current.style.left = `${posInitial}px`;
        }

        document.onmouseup = null;
        document.onmousemove = null;
      };

      const shiftSlide = (dir, action) => {
        itemsRef.current.classList.add('shifting');

        if (allowShift) {
          if (!action) {
            setPosInitial(itemsRef.current.offsetLeft);
          }

          if (dir === 1) {
            itemsRef.current.style.left = `${posInitial - itemsRef.current.offsetWidth}px`;
            setIndex(index + 1);
          } else if (dir === -1) {
            itemsRef.current.style.left = `${posInitial + itemsRef.current.offsetWidth}px`;
            setIndex(index - 1);
          }
        }

        setAllowShift(false);
      };

      const checkIndex = () => {
        itemsRef.current.classList.remove('shifting');

        if (index === -1) {
          itemsRef.current.style.left = `-${itemsRef.current.offsetWidth * (itemsRef.current.children.length - 2)}px`;
          setIndex(itemsRef.current.children.length - 3);
        }

        if (index === itemsRef.current.children.length - 2) {
          itemsRef.current.style.left = `-${itemsRef.current.offsetWidth}px`;
          setIndex(0);
        }

        setAllowShift(true);
      };

// slide(slider, sliderItems, prev, next);
  return <div id="slider" class="slider">
  <div class="wrapper">
    <div id="slides" class="slides">
      <span class="slide">Slide 1</span>
      <span class="slide">Slide 2</span>
      <span class="slide">Slide 3</span>
      <span class="slide">Slide 4</span>
      <span class="slide">Slide 5</span>
    </div>
  </div>
  <a id="prev" class="control prev"></a>
  <a id="next" class="control next"></a>
</div>
}};

export default Reviews;
