import React, { useEffect, useRef, useState } from 'react';
import './HighendAddon.css'
import range1 from '../Images/appliances/thermador-range.webp'
import cooktop from '../Images/appliances/jennair-cooktop.png'
import range2 from '../Images/appliances/viking-range.jpg'
import refrigerator from '../Images/appliances/true-refrigerator.jpg'

const HighendAddon = () => {
  const [isVisible, setIsVisible] = useState(false);
  const elementRef = useRef(null);

  const onIntersection = (entries) => {
    const [entry] = entries;
    setIsVisible(entry.isIntersecting);
  };
  useEffect(() => {
    const observer = new IntersectionObserver(onIntersection);
    if (elementRef.current) observer.observe(elementRef.current);
    return () => { if (elementRef.current) observer.unobserve(elementRef.current); };
  }, [elementRef]);

  return <div id="addons">
    <div id="addon-one">
      <div class="first-half">
        <div class="highend-addon-info info-left">
          <h1>We repair all brands and types of large gas and electric cooking appliances</h1>
        </div>
        <div class="image-right" id="addon-image-one">
          <div ref={elementRef} className={isVisible ? 'animate' : 'image-frame'}><img class="highend-addon-image" src={range1}/></div>
        </div>
      </div>
      <div class="second-half">
        <div class="image-left" id="addon-image-two">
        <div ref={elementRef} className={isVisible ? 'animate' : 'image-frame'}><img class="highend-addon-image" src={cooktop}/></div>
        </div>
        <div class="highend-addon-info info-right"> 
          <h1>Most repairs come with a one-year warranty, satisfaction guearanteed</h1>
        </div>
      </div>
    </div>
    <div id="addon-two">
      <div class="first-half">
        <div class="highend-addon-info info-left">
          <h1>We will take care of your stove, cooktop, and oven</h1>
        </div>
        <div class="image-right" id="addon-image-one">
        <div ref={elementRef} className={isVisible ? 'animate' : 'image-frame'}><img class="highend-addon-image" src={range2}/></div>
        </div>
      </div>
      <div class="second-half">
        <div class="image-left" id="addon-image-two">
        <div ref={elementRef} className={isVisible ? 'animate' : 'image-frame'}><img class="highend-addon-image" src={refrigerator}/></div>
        </div>
        <div class="highend-addon-info info-right"> 
          <h1>As well as of the refrigerator, wine cooler, or freezer</h1>
        </div>
      </div>
    </div>
  </div>
};

export default HighendAddon;
