// This is Header component /Navigation Component
import React from "react";
import logo from "/home/kubigor/react/Agency-website/src/MySite/12/Final-logo-alpha.png"
import styled from "styled-components";

const Headers = styled.header`
display: flex;
justify-content: space-between;
align-items: center;
padding: 1rem 5rem;
background-color: #050F66;
color: var(--white)
position: relative;
z-index: 500;
max-height: 4rem;


`;
const Logo = styled.a`
display: flex;
aligh-items: center;
max-width:30%;
hight:30%;
cursor:pointer;

img {
  margin-right: 1rem;
  min-width: 8rem;
}

`;
const Nav = styled.nav`
width: 25rem;
max-width: 40rem;
align-items: center;
align-content: space-between;
display: flex;
flex-direction: row;
column-gap: 1rem;

a{

  font-weight: 600;
  color: var(--white);
  line-height: 1.5;
  min-width: 4rem;


  &::after{
    content:"";
    display: block;
    height: 3px;
    width: 3px;
    background: transparent;
    transition: width 0.5s;
  }
    &:not(:last-child):hover::after{
    width:100%;
    background:#41D0FF;
  }
}

// .contact{
//   height: 10rem;
}
`
const Button = styled.button`
background-color: orange;
padding: 0.5rem 1rem;
border-radius: 20px;
// border: 3px;
color: white;
font-weight: 600;
cursor: pointer;
transition: all 0,5s
`

const Header = () => {
  return (
  <Headers>
    <div>
      <Logo>
        <img src={logo} alt="logo" />
      </Logo>
      </div>
      <Nav>
        <a href="#home">Home</a>
        <a href="#about">About</a>
        <a href="#services">Services</a>
        <a href="#contact">
          <Button>Request Service</Button>
        </a>
      </Nav>
      </Headers>
  )
};

export default Header;
