// This is HeroSection component, Main top section of website
import React from "react";
import styled from "styled-components"
import Kitchen from "../../Images/bosch-cocina.jpeg"

// const Section = styled.div`
// display:flex;
// justify-content:center;
// `
const Background = styled.div`
display:flex;
background-image: url(${Kitchen});
background-size: no-repeat;
background-position: center;
background-size: cover;
justify-content:right;
width: 100vw;
height:90vh;
margin-bottom: 1rem;

p{
  position:absolute;
  font-weight: 600;
  color white;
  text-align: right;
  width: 60%;
  font-size: 5vw;
  right: 5%;
  bottom: 20%;
}

@media only Screen and (max-width: 48em){
  background-position-x: -130px;

  p{
    text-align: center;
    font-size: 3rem;
    right: 0;
    width: 100%;
    bottom: 30%;
  }
}
`

const Title = styled.div`
position:absolute;
  font-weight: 600;
  color white;
  text-align: right;
  width: 60%;
  font-size: 5vw;
  right: 5%;
  bottom: 20%;

  @media only Screen and (max-width: 48em){
    text-align: center;
    font-size: 3rem;
    right: 0;
    width: 100%;
    bottom: 30%;
  }
`

const TopLayer = styled.div`
display:flex;
position:absolute;
transform: skew(-30deg);
height: 90%;
width: 80%;
right: -15%;
background: linear-gradient(270deg, rgba(0,0,0,0.6) 90%, rgba(0,0,0,0)) ;
backdrop-filter: blur(1px);
align-items: center;
padding: 3rem;

@media only Screen and (max-width: 48em){
  background: rgba(0,0,0,0.6);
  transform: skew(0);
  width: 100%;
  right: 0;
}
`

const HeroSection = () => {
  return <Background>
      <TopLayer>
      </TopLayer>
        <Title>
          <div><p id="text1">Fast in-home</p></div>
          <div><p id="text2">appliance repairs</p></div>
          <div><p id="text3">by certified experts</p></div>
        </Title>
    </Background>;
};

export default HeroSection;
