const { createGlobalStyle } = require('styled-components');

export const phoneNumber = "+1 425 600 zz6021"

export const GlobalStyle = createGlobalStyle`
:root{
    --orange:#FFC436;
    --deep-blue:#0C356A;
    --light-blue:#0174BE;
    --green:#4A8271;
    --peach:#FFF9EB;
    --light-grey:#ffe8b2;
  /* --orange:#FE8800;
  --deep-blue:#000432;
  --peach:#FFF9F6;
  --light-grey:#DADADA; */

*,*::before,*::after{
    margin:0;
    padding:0;
    box-sizing:border-box;
    font-family: 'Poppins', sans-serif;
}

html{
  scroll-behavior:smooth;
}
    body {
        overflow-x: hidden;
    }

    a {
        text-decoration: none;
    }

    img{
        width:100%;
    }
    `
