import React, { useEffect, useState, useRef } from "react";
import range from "../Images/appliances/bertazzoni-range.jpg"
import cooktop from "../Images/appliances/gas-cooktop.jpg"
import oven from "../Images/appliances/bosch-oven.jpg"
import disposal from "../Images/appliances/garbage-disposal.jpg"
import center from "../Images/appliances/laundry-center.jpg"

import "./Reviews.css"

const Reviews = ({ slides }) => {
  const sliderRef = useRef(null);
  const [index, setIndex] = useState(0);
  const threshold = 100;
  let posX1 = 0;
  let posX2 = 0;
  let posInitial;
  let posFinal;

  const slideSize = () => {
    return sliderRef.current?.getElementsByClassName('slide')[0]?.offsetWidth || 0;
  };


  const dragStart = (e) => {
    e.preventDefault();
    posInitial = sliderRef.current.offsetLeft;

    if (e.type === 'touchstart') {
      posX1 = e.touches[0].clientX;
    } else {
      posX1 = e.clientX;
      document.onmouseup = dragEnd;
      document.onmousemove = dragAction;
    }
  };

  const dragAction = (e) => {
    if (e.type === 'touchmove') {
      posX2 = posX1 - e.touches[0].clientX;
      posX1 = e.touches[0].clientX;
    } else {
      posX2 = posX1 - e.clientX;
      posX1 = e.clientX;
    }
    sliderRef.current.style.left = (sliderRef.current.offsetLeft - posX2) + "px";
  };

  const dragEnd = () => {
    posFinal = sliderRef.current.offsetLeft;
    if (posFinal - posInitial < -threshold) {
      shiftSlide(1);
    } else if (posFinal - posInitial > threshold) {
      shiftSlide(-1);
    } else {
      sliderRef.current.style.left = posInitial + "px";
    }

    document.onmouseup = null;
    document.onmousemove = null;
  };

  const shiftSlide = (dir) => {
    if (dir === 1) {
      setIndex((prevIndex) => prevIndex + 1);
    } else if (dir === -1) {
      setIndex((prevIndex) => prevIndex - 1);
    }
  };

 useEffect(() => {
    const slidesLength = slides.length;
    const currentSlideSize = slideSize();
    let newPosition = -(index + 1) * currentSlideSize;

    if (index === -1) {
      newPosition = -(slidesLength * currentSlideSize);
    } else if (index === slidesLength) {
      newPosition = -currentSlideSize;
    }

    if (sliderRef.current) {
      sliderRef.current.style.left = `${newPosition}px`;
    }
  }, [index, slides.length]);
  return (
    <div className="slider" ref={sliderRef}>
      <div className="slides">
        {slides.map((slide, idx) => (
          <div key={idx} className="slide">
            {slide}
          </div>
        ))}
      </div>
      <button className="prev" onClick={() => shiftSlide(-1)}>Previous</button>
      <button className="next" onClick={() => shiftSlide(1)}>Next</button>
    </div>
  );
};

export default Reviews;
