import React, {useEffect} from 'react'
import subzero from "../../Images/appliances/subzero-fridge.webp";
import viking from "../../Images/appliances/viking-fridge.jpeg";
import monogram from "../../Images/appliances/monogram-fridge.jpeg";
import bertazzoni from "../../Images/appliances/bertazzoni-fridge.webp";
import './HighEnds.css'

const HighEnds = () => {
  useEffect(() => {
    const shade = document.getElementById('first-section-shade');
    const menuHeight = document.getElementById('menu').clientHeight;
    const types = document.getElementById('types');
    const background = document.getElementById('first-section-background');

    shade.style.opacity = '.55';
    window.scrollTo(0, menuHeight);
    types.style.bottom = "4.25rem";
    types.addEventListener('click', (e) => {
      let newBackground = "../../Images/appliances/sub-zero-kitchen.jpg";
      switch (e.target.innerText) {
        case 'Sub Zero': newBackground = subzero; break;
        case 'Viking': newBackground = viking; break;
        case 'Monogram': newBackground = monogram; break;
        case 'Bertazzoni': newBackground = bertazzoni; break;
      }

      shade.style.opacity= "1";
      background.style.transitionDelay= "1s";
      background.style.backgroundImage = `url(${newBackground})`
      new Promise(resolve => setTimeout(shade.style.opacity= "0", 1000))
      shade.style.opacity= "0";

      console.log(background.style.backgroundImage)
      // shade.style.transitionDelay= "1s";
      // shade.style.opacity= "0";


    })
  }, []);

  return <div id="highend-container" >
      <div id="first-section-background" class="background">
        <div id="first-section-shade"></div>
        <div id="types">
            <span class="image-btn" id="btn1"><div class="image-btn-shade" value="1">Sub Zero </div></span>
            <span class="image-btn" id="btn2" ><div class="image-btn-shade" value="2">Viking</div></span>
            <span class="image-btn" id="btn3"><div class="image-btn-shade" value="3">Monogram</div></span>
            <span class="image-btn" id="btn4"><div class="image-btn-shade" value="4">Bertazzoni</div></span>
        </div>
      </div>
      <div class="divider"></div>
      <div id="second-section-background">
        <div id="second-section-shade"></div>
        <div>

        </div>
      </div>
      <div class="divider"></div>

    </div>
};

export default HighEnds;
